    del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT , o.name as "user.office.name" , (select m.is_read from wdt_t_task_message m where m.pin_id= a.coments_id and a.user_id=m.reciver_id ORDER BY m.update_date desc limit 1) as "readFlag" FROM wdt_t_task_comments_person a
left join sys_office o on u3.office_id=o.id
a.coments_id = #{comentsId}
