mapper.select:
SELECT 

FROM wdt_t_task_handover a

WHERE a.id = #{id}

SELECT 

FROM wdt_t_task_handover a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT 

FROM wdt_t_task_handover a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
