mapper.select:
SELECT 

FROM wdt_t_task_suggest a

WHERE a.id = #{id}

        SELECT 

        FROM wdt_t_task_suggest a


            a.del_flag = #{DEL_FLAG_NORMAL}

            AND (u.login_name like 
                    '%'||#{inputPerson.id}||'%'
                    '%'+#{inputPerson.id}+'%'
                    CONCAT('%', #{inputPerson.id}, '%') 

                    or  u.name like
	            '%'||#{inputPerson.id}||'%'
	            '%'+#{inputPerson.id}+'%'
	            CONCAT('%', #{inputPerson.id}, '%')
	            )


                AND a.type = #{type}


                AND a.input_date BETWEEN #{beginInputDate} AND #{endInputDate}




                ORDER BY ${page.orderBy}


                ORDER BY a.update_date DESC

SELECT 

FROM wdt_t_task_suggest a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE wdt_t_task_suggest SET 
    del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
